import React, { useState, useEffect, useRef } from 'react';

const KeyBenefits = () => {
  const [isVisible, setIsVisible] = useState(false);
  const sectionRef = useRef(null);

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          setIsVisible(true);
        }
      },
      {
        threshold: 0.1,
        rootMargin: '0px 0px -50px 0px'
      }
    );

    if (sectionRef.current) {
      observer.observe(sectionRef.current);
    }

    return () => {
      if (sectionRef.current) {
        observer.unobserve(sectionRef.current);
      }
    };
  }, []);

  const benefits = [
    {
      icon: "📊",
      title: "Reduce operational costs",
      description: "Boost productivity and reduce manual efforts for your operations team."
    },
    {
      icon: "⚡",
      title: "Minimize risk",
      description: "Reduce exposure to operational & counterparty risks by digitizing your unstructured data."
    },
    {
      icon: "👥",
      title: "Boost capacity",
      description: "Scale your operations without adding headcount as trading volume grows."
    },
    {
      icon: "🔒",
      title: "Protect sensitive data",
      description: "Enjoy peace of mind with maximum data security to safeguard sensitive information."
    },
    {
      icon: "⚙️",
      title: "Support custom workflows",
      description: "Handle bespoke processes without needing custom training or lengthy implementations."
    },
    {
      icon: "🔗",
      title: "Integrate seamlessly",
      description: "Gain immediate benefits, without needing to overhaul existing infrastructure or migrate your counterparties."
    }
  ];

  return (
    <section ref={sectionRef} className="py-20 bg-white">
      <div className="container mx-auto px-6">
        {/* Header */}
        <div className={`transform transition-all duration-1000 ease-out ${
          isVisible ? 'translate-y-0 opacity-100' : 'translate-y-8 opacity-0'
        }`}>
          <div className="text-center mb-16">
            <div className="text-blue-600 text-sm font-medium mb-4">Key Benefits</div>
            <h2 className="text-6xl font-light text-gray-900 mb-6 leading-tight">
              Supercharge your team with effortless automation
            </h2>
            <p className="text-xl text-gray-600">
              Revolutionize your workflow to reduce workloads once and for all.
            </p>
          </div>
        </div>

        {/* Benefits Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {benefits.map((benefit, index) => (
            <div
              key={index}
              className={`bg-white rounded-3xl p-10 border border-gray-100 transition-all duration-1000 ease-out ${
                isVisible ? 'translate-y-0 opacity-100' : 'translate-y-16 opacity-0'
              }`}
              style={{
                animationDelay: `${index * 200}ms`,
                animationFillMode: 'forwards',
                boxShadow: isVisible 
                  ? '0 10px 25px -5px rgba(59, 130, 246, 0.1), 0 10px 10px -5px rgba(59, 130, 246, 0.04)'
                  : '0 4px 6px -1px rgba(59, 130, 246, 0.1), 0 2px 4px -1px rgba(59, 130, 246, 0.06)'
              }}
            >
              {/* Icon */}
              <div className="text-4xl mb-6 text-blue-600">
                {benefit.icon}
              </div>

              {/* Title */}
              <h3 className="text-2xl font-light text-gray-900 mb-4">
                {benefit.title}
              </h3>

              {/* Description */}
              <p className="text-gray-600 leading-relaxed">
                {benefit.description}
              </p>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default KeyBenefits;
