import React, { useState, useEffect, useRef } from 'react';

const ExperimentationLifecycle = () => {
  const [isVisible, setIsVisible] = useState(false);
  const sectionRef = useRef(null);

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          setIsVisible(true);
        }
      },
      {
        threshold: 0.1,
        rootMargin: '0px 0px -50px 0px'
      }
    );

    if (sectionRef.current) {
      observer.observe(sectionRef.current);
    }

    return () => {
      if (sectionRef.current) {
        observer.unobserve(sectionRef.current);
      }
    };
  }, []);

  const steps = [
    {
      title: "Automate Experimentation",
      description: "Optivise continuously identifies opportunities for improvement across your website, intelligently suggesting changes that drive engagement and growth."
    },
    {
      title: "Power Decisions with Real Data",
      description: "We integrate directly with platforms like Statsig to collect and interpret real user behavior, turning interactions into clear insights and measurable outcomes."
    },
    {
      title: "Close the Experimentation Gap",
      description: "Our mission is to give every company â€” from startups to enterprises â€” the ability to test, learn, and evolve as fast as the industry leaders."
    },
    {
      title: "Generate",
      description: "Optivise automatically creates new design or feature variants for targeted areas of your site, enabling precise experimentation."
    },
    {
      title: "Execute",
      description: "Those variants are deployed live to real users based on admin-defined traffic thresholds, ensuring balanced and unbiased testing."
    },
    {
      title: "Analyze",
      description: "As data flows in, Optivise aggregates user interactions like clicks and conversions, revealing which experiences truly perform best."
    },
    {
      title: "Push",
      description: "With one approval, admins can promote winning variants directly to production â€” creating a site that continuously optimizes itself."
    }
  ];

  return (
    <section ref={sectionRef} className="py-20 bg-white">
      <div className="container mx-auto px-6">
        {/* Header */}
        <div className={`transform transition-all duration-1000 ease-out ${
          isVisible ? 'translate-y-0 opacity-100' : 'translate-y-8 opacity-0'
        }`}>
          <div className="text-left mb-16">
            <div className="text-blue-600 text-sm font-medium mb-2">ðŸ’¡ What We Do</div>
            <h2 className="text-6xl font-light text-gray-900 mb-6 leading-tight">
              Optivise empowers teams to experiment like the world's biggest companies â€” without the complexity.
            </h2>
          </div>
        </div>

        {/* Steps Grid - 3 top, 4 bottom */}
        <div className="space-y-8">
          {/* Top 3 boxes */}
          <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
            {steps.slice(0, 3).map((step, index) => (
              <div
                key={index}
                className={`bg-white rounded-3xl p-10 border border-gray-100 transition-all duration-1000 ease-out ${
                  isVisible ? 'translate-y-0 opacity-100' : 'translate-y-16 opacity-0'
                }`}
                style={{
                  animationDelay: `${index * 200}ms`,
                  animationFillMode: 'forwards',
                  boxShadow: isVisible 
                    ? '0 10px 25px -5px rgba(59, 130, 246, 0.1), 0 10px 10px -5px rgba(59, 130, 246, 0.04)'
                    : '0 4px 6px -1px rgba(59, 130, 246, 0.1), 0 2px 4px -1px rgba(59, 130, 246, 0.06)'
                }}
              >
                <h3 className="text-2xl font-light text-gray-900 mb-4">
                  {step.title}
                </h3>
                <p className="text-gray-600 leading-relaxed">
                  {step.description}
                </p>
              </div>
            ))}
          </div>

          {/* Bottom 4 boxes */}
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8">
            {steps.slice(3, 7).map((step, index) => (
              <div
                key={index + 3}
                className={`bg-white rounded-3xl p-10 border border-gray-100 transition-all duration-1000 ease-out ${
                  isVisible ? 'translate-y-0 opacity-100' : 'translate-y-16 opacity-0'
                }`}
                style={{
                  animationDelay: `${(index + 3) * 200}ms`,
                  animationFillMode: 'forwards',
                  boxShadow: isVisible 
                    ? '0 10px 25px -5px rgba(59, 130, 246, 0.1), 0 10px 10px -5px rgba(59, 130, 246, 0.04)'
                    : '0 4px 6px -1px rgba(59, 130, 246, 0.1), 0 2px 4px -1px rgba(59, 130, 246, 0.06)'
                }}
              >
                <h3 className="text-2xl font-light text-gray-900 mb-4">
                  {step.title}
                </h3>
                <p className="text-gray-600 leading-relaxed">
                  {step.description}
                </p>
              </div>
            ))}
          </div>
        </div>
      </div>

    </section>
  );
};

export default ExperimentationLifecycle;
