import React, { useState, useEffect } from 'react';

const ExperimentationLifecycle = () => {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    const timer = setTimeout(() => {
      setIsVisible(true);
    }, 200);

    return () => clearTimeout(timer);
  }, []);

  const steps = [
    {
      title: "Generate Experiments Automatically",
      description: "Optivise continuously scans your website and generates potential experiments — creating multiple design or layout variants to discover what resonates best with users."
    },
    {
      title: "Deploy to Real Users Instantly",
      description: "Once variants are created, Optivise deploys them live to real users, intelligently balancing traffic and capturing authentic behavior data in real time."
    },
    {
      title: "Analyze with Real User Data",
      description: "Through integrations with Statsig and other analytics tools, Optivise measures impressions, clicks, and engagement — turning interaction data into actionable insights."
    },
    {
      title: "Push Winning Variants Seamlessly",
      description: "Admins can approve or reject experiments directly in Optivise, automatically rolling out winning versions to production with a single action."
    },
    {
      title: "Learn and Evolve Continuously",
      description: "Every experiment feeds back into the AI engine, helping Optivise learn which types of changes drive impact — so every future variant gets smarter."
    }
  ];

  return (
    <section className="py-20 bg-white">
      <div className="container mx-auto px-6">
        {/* Header */}
        <div className={`transform transition-all duration-1000 ease-out ${
          isVisible ? 'translate-y-0 opacity-100' : 'translate-y-8 opacity-0'
        }`}>
          <div className="text-center mb-16">
            <h2 className="text-6xl font-light text-gray-900 mb-6 leading-tight">
              Optivise automates the entire experimentation lifecycle — from generating ideas to deploying winning experiences.
            </h2>
          </div>
        </div>

        {/* Steps Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {steps.map((step, index) => (
            <div
              key={index}
              className={`bg-white rounded-3xl p-10 shadow-sm hover:shadow-lg transition-all duration-500 border border-gray-100 ${
                isVisible ? 'animate-popIn' : 'opacity-0'
              }`}
              style={{
                animationDelay: `${index * 200}ms`,
                animationFillMode: 'forwards',
                boxShadow: '0 4px 6px -1px rgba(59, 130, 246, 0.1), 0 2px 4px -1px rgba(59, 130, 246, 0.06)'
              }}
            >
              {/* Title */}
              <h3 className="text-2xl font-light text-gray-900 mb-4">
                {step.title}
              </h3>

              {/* Description */}
              <p className="text-gray-600 leading-relaxed">
                {step.description}
              </p>
            </div>
          ))}
        </div>
      </div>

      <style jsx>{`
        @keyframes popIn {
          0% {
            opacity: 0;
            transform: scale(0.3) translateY(20px);
          }
          50% {
            opacity: 1;
            transform: scale(1.05) translateY(-5px);
          }
          100% {
            opacity: 1;
            transform: scale(1) translateY(0);
          }
        }
      `}</style>
    </section>
  );
};

export default ExperimentationLifecycle;
