import React, { useState, useEffect, useRef } from 'react';

const ExperimentationLifecycle = () => {
  const [isVisible, setIsVisible] = useState(false);
  const sectionRef = useRef(null);

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          setIsVisible(true);
        }
      },
      {
        threshold: 0.1,
        rootMargin: '0px 0px -50px 0px'
      }
    );

    if (sectionRef.current) {
      observer.observe(sectionRef.current);
    }

    return () => {
      if (sectionRef.current) {
        observer.unobserve(sectionRef.current);
      }
    };
  }, []);

  const steps = [
    {
      title: "AI-Driven Optimization",
      description: "Leveraging AI to eliminate inefficiencies in product development and empower teams to focus on high-impact work.",
      icon: "ü§ñ"
    },
    {
      title: "Automation",
      description: "Automation and AI-driven decision-making can revolutionize how UI/UX optimization is done.",
      icon: "‚ö°"
    },
    {
      title: "Time Efficiency",
      description: "We've seen firsthand how small UI changes consume disproportionate time and resources.",
      icon: "‚è±Ô∏è"
    },
    {
      title: "Accessibility",
      description: "Making data-driven UI optimization faster and accessible to all companies.",
      icon: "üåê"
    }
  ];

  return (
    <section ref={sectionRef} className="py-20 bg-white">
      <div className="container mx-auto px-6">
        {/* Header */}
        <div className={`transform transition-all duration-1000 ease-out ${
          isVisible ? 'translate-y-0 opacity-100' : 'translate-y-8 opacity-0'
        }`}>
          <div className="text-left mb-16">
            <div className="text-blue-600 text-sm font-medium mb-2">What We Do</div>
            <h2 className="text-6xl font-light text-gray-900 mb-6 leading-tight">
              Optivise empowers teams to experiment like the world's biggest companies ‚Äî without the complexity.
            </h2>
          </div>
        </div>

        {/* Steps Grid - 2x2 grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
          {steps.map((step, index) => (
            <div
              key={index}
              className={`bg-white rounded-2xl p-8 border border-gray-100 transition-all duration-1000 ease-out hover:shadow-lg group ${
                isVisible ? 'translate-y-0 opacity-100' : 'translate-y-16 opacity-0'
              }`}
              style={{
                animationDelay: `${index * 200}ms`,
                animationFillMode: 'forwards'
              }}
            >
              <div className="flex items-start justify-between mb-6">
                <div className="text-4xl">{step.icon}</div>
                <div className="text-gray-400 group-hover:text-blue-600 transition-colors duration-300">
                  <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
                  </svg>
                </div>
              </div>
              <h3 className="text-xl font-semibold text-gray-900 mb-3">
                {step.title}
              </h3>
              <p className="text-gray-600 leading-relaxed text-sm">
                {step.description}
              </p>
            </div>
          ))}
        </div>
      </div>

    </section>
  );
};

export default ExperimentationLifecycle;
