import React from 'react';

const HowItWorks = () => {
  const steps = [
    {
      icon: "üß©",
      title: "Generate",
      description: "Optivise continuously analyzes your website and generates potential experiments ‚Äî automatically creating multiple variants of the same feature or layout for more accurate testing across user experiences."
    },
    {
      icon: "üöÄ",
      title: "Execute", 
      description: "Those variants are deployed to real users based on thresholds defined by the admin. During this phase, Optivise integrates with analytics platforms like Statsig to capture real user interactions ‚Äî impressions, clicks, and engagement data."
    },
    {
      icon: "üìä",
      title: "Analyze",
      description: "As data flows in, Optivise aggregates and summarizes variant performance, delivering clear, actionable insights on what's working and what's not."
    },
    {
      icon: "‚úÖ",
      title: "Push",
      description: "Admins can approve or reject experiments directly within Optivise, seamlessly promoting winning variants to production and letting the site evolve continuously."
    }
  ];

  return (
    <section className="bg-black py-20">
      <div className="container mx-auto px-6">
        {/* Header */}
        <div className="text-center mb-16">
          <h2 className="text-5xl font-bold text-white mb-4">
            ‚öôÔ∏è How does it work?
          </h2>
          <p className="text-xl text-gray-300">
            Experience automated experimentation from creation to production.
          </p>
        </div>

        {/* Steps Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8">
          {steps.map((step, index) => (
            <div key={index} className="bg-gray-900 rounded-2xl p-8 border border-gray-800 hover:border-gray-700 transition-all duration-300">
              {/* Icon */}
              <div className="w-16 h-16 bg-gray-800 rounded-full flex items-center justify-center mb-6 mx-auto">
                <span className="text-3xl">{step.icon}</span>
              </div>

              {/* Title */}
              <h3 className="text-xl font-bold text-white text-center mb-4">
                {step.title}
              </h3>

              {/* Description */}
              <p className="text-gray-300 text-center leading-relaxed">
                {step.description}
              </p>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default HowItWorks;
