import React, { useState, useEffect } from 'react';

const Hero = () => {
  const [displayText, setDisplayText] = useState('');
  const fullText = "AI-powered experimentation to everyone — fast, intelligent, and effortless";

  useEffect(() => {
    let index = 0;
    const timer = setInterval(() => {
      if (index < fullText.length) {
        setDisplayText(fullText.slice(0, index + 1));
        index++;
      } else {
        clearInterval(timer);
      }
    }, 50); // Adjust speed as needed

    return () => clearInterval(timer);
  }, []);

  return (
    <section className="relative h-[70vh] border border-gray-300 rounded-4xl overflow-hidden mt-1">
      <div className="flex h-full border-t border-gray-300 items-center justify-center">
        <div className="text-center">
          <h2 className="text-6xl font-light mb-6 text-black leading-tight">
            {displayText}
            <span className="animate-pulse">|</span>
          </h2>
          <p className="text-2xl font-light text-gray-600">
            For developers by the developers
          </p>
        </div>
      </div>
    </section>
  );
};

export default Hero;
