import React, { useState, useEffect } from 'react';

const HowItWorks = () => {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    const timer = setTimeout(() => {
      setIsVisible(true);
    }, 200);

    return () => clearTimeout(timer);
  }, []);

  const steps = [
    {
      number: "01",
      title: "Smart Detection",
      description: "Our AI scans your application to identify optimization opportunities and potential experiment variations automatically."
    },
    {
      number: "02", 
      title: "Intelligent Deployment",
      description: "Seamlessly deploy experiments to your users with intelligent traffic splitting and real-time monitoring capabilities."
    },
    {
      number: "03",
      title: "Data-Driven Insights",
      description: "Get comprehensive analytics and statistical significance reports to make informed decisions about your experiments."
    }
  ];

  return (
    <section className="py-20 bg-gray-50">
      <div className="container mx-auto px-6">
        {/* Header */}
        <div className={`transform transition-all duration-1000 ease-out ${
          isVisible ? 'translate-y-0 opacity-100' : 'translate-y-8 opacity-0'
        }`}>
          <div className="text-center mb-16">
            <div className="text-blue-600 text-sm font-medium mb-4 tracking-wider uppercase">Our Process</div>
            <h2 className="text-6xl font-light text-gray-900 mb-6 leading-tight">
              How Optivise Works
            </h2>
            <p className="text-xl text-gray-600 max-w-3xl mx-auto">
              Transform your experimentation workflow with intelligent automation and data-driven insights.
            </p>
          </div>
        </div>

        {/* Steps Grid */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
          {steps.map((step, index) => (
            <div 
              key={index} 
              className={`bg-white rounded-3xl p-10 shadow-lg hover:shadow-xl transition-all duration-500 border border-gray-100 ${
                isVisible ? 'animate-popIn' : 'opacity-0 scale-0'
              }`}
              style={{
                animationDelay: `${index * 200}ms`,
                animationFillMode: 'forwards',
                boxShadow: '0 10px 25px -5px rgba(59, 130, 246, 0.1), 0 10px 10px -5px rgba(59, 130, 246, 0.04)'
              }}
            >
              {/* Number */}
              <div className="text-6xl font-light text-blue-600 mb-6 leading-none">
                {step.number}
              </div>

              {/* Title */}
              <h3 className="text-2xl font-light text-gray-900 mb-4">
                {step.title}
              </h3>

              {/* Description */}
              <p className="text-gray-600 leading-relaxed">
                {step.description}
              </p>
            </div>
          ))}
        </div>
      </div>

      <style jsx>{`
        @keyframes popIn {
          0% {
            opacity: 0;
            transform: scale(0.3) translateY(20px);
          }
          50% {
            opacity: 1;
            transform: scale(1.05) translateY(-5px);
          }
          100% {
            opacity: 1;
            transform: scale(1) translateY(0);
          }
        }
      `}</style>
    </section>
  );
};

export default HowItWorks;
