import React from 'react';

const HowItWorks = () => {
  const steps = [
    {
      title: "Generate",
      description: "Optivise continuously analyzes your website and generates potential experiments — automatically creating multiple variants of the same feature or layout for more accurate testing across user experiences."
    },
    {
      title: "Execute", 
      description: "Those variants are deployed to real users based on thresholds defined by the admin. During this phase, Optivise integrates with analytics platforms like Statsig to capture real user interactions — impressions, clicks, and engagement data."
    },
    {
      title: "Analyze",
      description: "As data flows in, Optivise aggregates and summarizes variant performance, delivering clear, actionable insights on what's working and what's not."
    },
    {
      title: "Push",
      description: "Admins can approve or reject experiments directly within Optivise, seamlessly promoting winning variants to production and letting the site evolve continuously."
    }
  ];

  return (
    <section className="py-20 bg-white">
      <div className="container mx-auto px-6">
        {/* Header */}
        <div className="text-center mb-16">
          <h2 className="text-5xl font-light text-gray-900 mb-4">
            How does it work?
          </h2>
          <p className="text-xl text-gray-600">
            Experience automated experimentation from creation to production.
          </p>
        </div>

        {/* Steps Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8">
          {steps.map((step, index) => (
            <div key={index} className="bg-white border border-gray-200 rounded-2xl p-8 hover:border-gray-300 hover:shadow-lg transition-all duration-300">
              {/* Title */}
              <h3 className="text-2xl font-medium text-gray-900 mb-4">
                {step.title}
              </h3>

              {/* Description */}
              <p className="text-gray-600 leading-relaxed">
                {step.description}
              </p>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default HowItWorks;
