import React, { useState, useEffect } from 'react';

const HowItWorks = () => {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    const timer = setTimeout(() => {
      setIsVisible(true);
    }, 200);

    return () => clearTimeout(timer);
  }, []);

  const steps = [
    {
      title: "Generate",
      description: "Optivise continuously analyzes your website and generates potential experiments — automatically creating multiple variants of the same feature or layout for more accurate testing across user experiences."
    },
    {
      title: "Execute", 
      description: "Those variants are deployed to real users based on thresholds defined by the admin. During this phase, Optivise integrates with analytics platforms like Statsig to capture real user interactions — impressions, clicks, and engagement data."
    },
    {
      title: "Analyze",
      description: "As data flows in, Optivise aggregates and summarizes variant performance, delivering clear, actionable insights on what's working and what's not."
    }
  ];

  return (
    <section className="py-20 bg-white">
      <div className="container mx-auto px-6">
        {/* Header */}
        <div className={`transform transition-all duration-1000 ease-out ${
          isVisible ? 'translate-y-0 opacity-100' : 'translate-y-8 opacity-0'
        }`}>
          <div className="text-left mb-12">
            <div className="text-blue-600 text-sm font-medium mb-2">What we do</div>
            <h2 className="text-6xl font-light text-gray-900 mb-6 leading-tight">
              Automate your entire experimentation process
            </h2>
            <p className="text-xl text-gray-600">
              Your development teams shouldn't be doing manual experiment creation and validation.
            </p>
          </div>
        </div>

        {/* Steps Grid - No gaps, connected */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-0">
          {steps.map((step, index) => (
            <div 
              key={index} 
              className={`bg-white border border-gray-200 rounded-none p-8 hover:bg-gray-50 transition-all duration-500 ${
                index === 0 ? 'rounded-l-2xl' : ''
              } ${
                index === steps.length - 1 ? 'rounded-r-2xl' : ''
              } ${
                index !== steps.length - 1 ? 'border-r-0' : ''
              }`}
              style={{
                animationDelay: `${index * 200}ms`,
                animation: isVisible ? `slideInUp 0.6s ease-out forwards` : 'none'
              }}
            >
              {/* Icon */}
              <div className="w-12 h-12 bg-blue-600 rounded-lg flex items-center justify-center mb-6">
                <span className="text-white text-xl font-bold">
                  {index + 1}
                </span>
              </div>

              {/* Title */}
              <h3 className="text-2xl font-light text-gray-900 mb-4">
                {step.title}
              </h3>

              {/* Description */}
              <p className="text-gray-600 leading-relaxed">
                {step.description}
              </p>
            </div>
          ))}
        </div>
      </div>

      <style jsx>{`
        @keyframes slideInUp {
          from {
            opacity: 0;
            transform: translateY(30px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }
      `}</style>
    </section>
  );
};

export default HowItWorks;
